import React, { useState } from "react";
import ToDoForm from "./ToDoForm";

const ToDoWrapper = () => {
  const [todos, setTodos] = useState([]);
  const addTodo = todo => {
    setTodos([...todos, { id: uuidv4(), task: todo }]);
  };
  return (
    <div className="TodoWrapper">
      <ToDoForm addTodo={addTodo} />
    </div>
  );
};

export default ToDoWrapper;
